<?phpclass Controlador {    function __construct() {        date_default_timezone_set('America/Bogota');        session_start();    }    public function cargarPagina() {        if (isset($_GET["modulo"])) {            $modulo = $_GET["modulo"];        } else {            $modulo = "inicio";        }        if ($modulo === 'killsession'){            return $this->cerrarCesion();        }        if ($this->validarSesion($modulo)){            echo $this->cargarHTML($modulo);        } else {            echo $this->cargarLogin('inicio');        }    }        private function validarSesion($modulo){        $token = isset($_SESSION['usuario_id']) ? $_SESSION['usuario_id'] : null;        if (!$token || $token === ''){            return false;        }        return true;    }    private function cargarHTML($ms) {        $archivo = $ms;        $mod = $ms;        if (strpos($ms, '/')) {            $submodulo = substr($ms, strpos($ms, '/') + 1);            $archivo = substr($ms, 0, strpos($ms, '/'));            $archivo = 'modulo/' . $submodulo . '/' . substr($ms, strpos($ms, '/') + 1);            $mod = substr($ms, 0, strpos($ms, '/'));        }        $usuario = $_SESSION['usuario_id'];                  $perfil = $_SESSION['perfil'];           $pagina = file_get_contents('app/vista/pagmaestra.html');        $modulo = file_get_contents('app/vista/forma/' . $mod . '/' . $archivo . '.html');        $menu = null;                if ($perfil === 'A'){            $menu = file_get_contents('app/vista/assets/general/menu.admin.html');        } elseif ($perfil === 'E') {                        $menu = file_get_contents('app/vista/assets/general/menu.user.e.html');        } else {            $menu = file_get_contents('app/vista/assets/general/menu.user.c.html');        }        $pagina = str_replace('##MENUPRINCIPAL##', $menu, $pagina);        $pagina = str_replace('##CONTENIDOS##', $modulo, $pagina);        $pagina = str_replace('##MODULO##', $archivo, $pagina);        $pagina = str_replace('##MODULOJS##', $mod, $pagina);        return $pagina;    }        private function cargarLogin($ms){                $archivo = $ms;        $mod = $ms;        if (strpos($ms, '/')) {            $submodulo = substr($ms, strpos($ms, '/') + 1);            $archivo = substr($ms, 0, strpos($ms, '/'));            $archivo = 'modulo/' . $submodulo . '/' . substr($ms, strpos($ms, '/') + 1);            $mod = substr($ms, 0, strpos($ms, '/'));        }        $pagina = file_get_contents('app/vista/pagmaestra.html');        $modulo = file_get_contents('app/vista/forma/inicio/login.html');        $pagina = str_replace('##MENUPRINCIPAL##', '', $pagina);        $pagina = str_replace('##CONTENIDOS##', $modulo, $pagina);        $pagina = str_replace('##MODULO##', $archivo, $pagina);        $pagina = str_replace('##MODULOJS##', $mod, $pagina);        return $pagina;    }        public function cerrarCesion(){        foreach($_SESSION as $key => $value){            unset($_SESSION[$key]);        }        header('Location: /');        return true;    }        }