<?phprequire __DIR__ . "/../../data/db.php";class temarioModel {    private $result;    private $primaryTable;    function __construct($active) {        $active ? true : die('ACCESS DENIED');        $this->dbo = new db();        $this->primaryTable = "temario";    }    private $dbo;    public function consultar($P) {        if (isset($P['_conse']) && !is_numeric($P['_conse'])) {            $this->setResult('Error al consultar el registro', 'error');            return false;        }                        $consulta = "";        switch ($P['tipo']) {            case "activo":                $consulta = "select id                                                "                        . "from $this->primaryTable "                        . "                                                     ";                break;            case "formulario":                $consulta = "select * from $this->primaryTable where id=" . $P['_conse'];                break;            case "consultar_modulo_id":                $consulta = "select * from temario where modulo_id = " . $P['modulo_id'];                break;        }        $this->dbo->conectar();        $res = $this->dbo->consultar($consulta);        $this->dbo->desconectar();        if ($this->dbo->getError() === null) {            $this->setResult($res, 'data');        } else {            $this->setResult("Ha ocurrido un error en la consulta", "error");            return false;        }        return true;    }    public function guardar($P) {        $temarioExiste = $this->temarioExiste($P['modulo_id']);        if (!isset($temarioExiste[0]['id'])) {            $consulta = "insert into $this->primaryTable(                                            nivel                                            ,ayudaPaginaWeb                                            ,ayudaVideos                                            ,ayudaPresentaciones                                            ,ayudaLibros                                            ,ayudaApuntesClase                                            ,ayudaOtros                                            ,ayudaCompas                                            ,ayudaProfesor                                            ,ayudaFamiliares                                            ,ayudaAmigos                                            ,dedicacionHoras                                            ,dedicacionDias                                            ,usuario_id                                            ,modulo_id                                            )                              values('" . $P["nivel"] . "',                                            '" . $P["ayudaPaginaWeb"] . "',                                            '" . $P["ayudaVideos"] . "',                                            '" . $P["ayudaPresentaciones"] . "',                                            '" . $P["ayudaLibros"] . "',                                            '" . $P["ayudaApuntesClase"] . "',                                            '" . $P["ayudaOtros"] . "',                                            '" . $P["ayudaCompas"] . "',                                            '" . $P["ayudaProfesor"] . "',                                            '" . $P["ayudaFamiliares"] . "',                                            '" . $P["ayudaAmigos"] . "',                                            '" . $P["dedicacionHoras"] . "',                                            '" . $P["dedicacionDias"] . "',                                            '" . $_SESSION['usuario_id'] . "',                                            '" . $P["modulo_id"] . "'                                            )										                                    ";        } else {            $consulta = "update $this->primaryTable set                            nivel = '" . $P['nivel'] . "'                            ,ayudaPaginaWeb = '" . $P['ayudaPaginaWeb'] . "'                            ,ayudaVideos = '" . $P['ayudaVideos'] . "'                            ,ayudaPresentaciones = '" . $P['ayudaPresentaciones'] . "'                            ,ayudaLibros = '" . $P['ayudaLibros'] . "'                            ,ayudaApuntesClase = '" . $P['ayudaApuntesClase'] . "'                            ,ayudaOtros = '" . $P['ayudaOtros'] . "'                            ,ayudaCompas = '" . $P['ayudaCompas'] . "'                            ,ayudaProfesor = '" . $P['ayudaProfesor'] . "'                            ,ayudaFamiliares = '" . $P['ayudaFamiliares'] . "'                            ,ayudaAmigos = '" . $P['ayudaAmigos'] . "'                            ,dedicacionHoras = '" . $P['dedicacionHoras'] . "'                            ,dedicacionDias = '" . $P['dedicacionDias'] . "'                        where id = " . $temarioExiste[0]['id'] . "                    ";        }        $this->dbo->conectar();        if (!$this->dbo->ejecutar($consulta)) {            $this->setResult('Error al guardar el registro', 'error');        } else {            $this->setResult('Tus metas han sido guardadas');        }        $this->dbo->desconectar();        return true;    }    public function editar($P) {        if (!is_numeric($P['id'])) {            $this->setResult('No se encuentra identificador', 'error');            return false;        }        $consulta = "update $this->primaryTable set                             nombre = '" . $P['nombre'] . "'                            ,descripcion = '" . $P['descripcion'] . "'                            ,modulo_id = '" . $P['modulo_id'] . "'                            ,estado = '" . $P['estado'] . "'            where id= " . $P['id'] . "                    ";        $this->dbo->conectar();        if (!$this->dbo->ejecutar($consulta)) {            $this->setResult('Error al editar el registro', 'error');        } else {            $this->setResult($this->dbo->getInsertedID(), 'ok');        }        $this->dbo->desconectar();        return true;    }    public function borrar($P) {        if (!is_numeric($P['id'])) {            $this->setResult('Error al borrar el registro', 'error');            return false;        }        $consulta = "update $this->primaryTable set estado = 'I'                                        where id= " . $P['id'] . "                    ";        $this->dbo->conectar();        if (!$this->dbo->ejecutar($consulta)) {            $this->setResult('Error al editar el registro', 'error');        } else {            $this->setResult($this->dbo->getInsertedID(), 'ok');        }        $this->dbo->desconectar();    }    public function setResult($output, $type = "message") {        $this->result = [            "type" => $type,            "output" => $output        ];        return true;    }    public function getResult() {        return $this->result;    }    private function temarioExiste($modulo_id) {        $consulta = "select id from temario where modulo_id=" . $modulo_id . " and usuario_id=" . $_SESSION['usuario_id'];        $this->dbo->conectar();        $modulo_id = $this->dbo->consultar($consulta);        $this->dbo->desconectar();        return $modulo_id;    }}